Fn::Sub:
{
  "Comment": "Nist CCC StepFunction.",
  "StartAt": "Contact or Register or Cred",
  "States": {
    "Contact or Register or Cred": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.inputs.check",
          "StringMatches": "register",
          "Next": "Check User Email"
        },
        {
          "Variable": "$.inputs.check",
          "StringMatches": "contact",
          "Next": "Contact"
        }
      ]
    },
    "Check User Email": {
      "Type": "Task",
      "Resource": "arn:aws:states:::dynamodb:getItem",
      "Parameters": {
        "TableName": "Register_Event_User",
        "Key": {
          "emailId": {
            "S.$": "$.inputs.email"
          }
        }
      },
      "ResultPath": "$.Items",
      "Next": "Filter Operation"
    },
    "Filter Operation": {
      "Type": "Choice",
      "Default": "Get User Data",
      "Choices": [
        {
          "Variable": "$.inputs.verify",
          "IsPresent": true,
          "Next": "Verify & Send Data"
        },
        {
          "Not": {
            "Variable": "$.Items.Item",
            "IsPresent": true
          },
          "Next": "Register User"
        }
      ]
    },
    "Verify & Send Data": {
      "Type": "Pass",
      "OutputPath": "$.Items",
      "End": true
    },
    "Register User": {
      "Type": "Task",
      "Resource": "arn:aws:states:::dynamodb:putItem",
      "Parameters": {
        "TableName": "Register_Event_User",
        "Item": {
          "emailId": {
            "S.$": "$.inputs.email"
          },
          "name": {
            "S.$": "$.inputs.name"
          },
          "branch": {
            "S.$": "$.inputs.branch"
          },
          "rollno": {
            "S.$": "$.inputs.rollno"
          },
          "batch": {
            "S.$": "$.inputs.batch"
          },
          "contact": {
            "S.$": "$.inputs.contact"
          },
          "ticketId": {
            "S.$": "$.inputs.ticket"
          },
          "residence": {
            "S.$": "$.inputs.residence"
          },
          "parent_contact": {
            "S.$": "$.inputs.parent_contact"
          },
          "address": {
            "S.$": "$.inputs.address"
          },
          "section": {
            "S.$": "$.inputs.section"
          }
        }
      },
      "ResultPath": "$.Items",
      "Next": "Email Sender"
    },
    "Email Sender": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage",
      "Parameters": {
        "MessageBody.$": "$.inputs",
        "QueueUrl": "${a15dd10806aa7b6b8464199dd1fc9b15}"
      },
      "End": true
    },
    "Get User Data": {
      "Type": "Pass",
      "OutputPath": "$.Items",
      "End": true
    },
    "Contact": {
      "Type": "Parallel",
      "End": true,
      "Branches": [
        {
          "StartAt": "Contact Email Sender",
          "States": {
            "Contact Email Sender": {
              "Type": "Task",
              "Resource": "arn:aws:states:::sqs:sendMessage",
              "Parameters": {
                "MessageBody.$": "$",
                "QueueUrl": "${a15dd10806aa7b6b8464199dd1fc9b15}"
              },
              "End": true
            }
          }
        },
        {
          "StartAt": "Save Contact Data",
          "States": {
            "Save Contact Data": {
              "Type": "Task",
              "Resource": "arn:aws:states:::dynamodb:putItem",
              "Parameters": {
                "TableName": "NIST_CCC_Contact",
                "Item": {
                  "emailId": {
                    "S.$": "$.inputs.email"
                  },
                  "fullname": {
                    "S.$": "$.inputs.fullname"
                  },
                  "message": {
                    "S.$": "$.inputs.message"
                  }
                }
              },
              "OutputPath": "$.SdkResponseMetadata",
              "End": true
            }
          }
        }
      ]
    }
  }
}
